```js
(async () => {
  // ===== CONFIG: update ONLY the operationId string below =====
  const GRAPHQL_ENDPOINT = "/graphql"; // same origin, relative path is fine

  const baseBody = {
    operationName: "menusDish",
    variables: {
      menuItemId: null   // we override this per card
    },
    extensions: {
      // 👉 copy the exact value from the Network tab's menusDish request
      operationId: "PopmenuClient/0f13f56760eablaef13c415f7c22d35e"
    }
  };

  // If your menusDish request includes any *extra* variables (rare),
  // add them into baseBody.variables above.

  // ===== helper: deep clone + set menuItemId =====
  function buildRequestBody(menuItemId) {
    const body = JSON.parse(JSON.stringify(baseBody));
    body.variables.menuItemId = menuItemId;
    return body;
  }

  // ===== helper: build short tag text from modifier groups =====
  function buildTagFromModifierGroups(groups) {
    if (!groups || !groups.length) return "No modifiers";

    // Example strategy: just show group names.
    // You can filter/rename here if you only care about certain groups.
    const names = groups
      .map(g => g && g.name)
      .filter(Boolean);

    return names.join(" • ");
  }

  // ===== helper: inject tag into the *right side* of the row =====
  function injectTagIntoCard(card, tagText) {
    const actionArea = card.querySelector(".MuiCardHeader-action-2134");
    if (!actionArea) return;

    // avoid duplicates if you rerun the script
    let tag = actionArea.querySelector(".__mod-tags");
    if (!tag) {
      tag = document.createElement("div");
      tag.className = "__mod-tags";

      tag.style.fontSize = "11px";
      tag.style.marginRight = "8px";
      tag.style.marginLeft = "8px";
      tag.style.whiteSpace = "nowrap";
      tag.style.maxWidth = "220px";
      tag.style.overflow = "hidden";
      tag.style.textOverflow = "ellipsis";
      tag.style.opacity = "0.75";
      tag.style.alignSelf = "center";

      // Insert before the grid of icon buttons so it sits in the blank area
      actionArea.insertBefore(tag, actionArea.firstChild);
    }

    tag.textContent = tagText;
  }

  // ===== main: loop through all menu cards on the page =====
  const cards = Array.from(
    document.querySelectorAll('[data-cy="menu_item_card"]')
  );

  console.log(`Found ${cards.length} menu items`);

  for (const card of cards) {
    // Get id from DOM:
    //   id="30236836-menu-item"  OR  data-lazy-item-id="30236836"
    const rawId =
      card.getAttribute("data-lazy-item-id") ||
      (card.id || "").split("-")[0];

    const menuItemId = Number(rawId);
    if (!menuItemId) {
      console.warn("Could not parse menu item id for card", card);
      continue;
    }

    try {
      const res = await fetch(GRAPHQL_ENDPOINT, {
        method: "POST",
        headers: {
          "content-type": "application/json"
          // If the original menusDish request has extra headers (e.g. x-csrf-token),
          // copy them in here from the Network tab.
        },
        body: JSON.stringify(buildRequestBody(menuItemId)),
        credentials: "include" // send your existing cookies/session
      });

      if (!res.ok) {
        console.warn("menusDish failed for", menuItemId, res.status);
        continue;
      }

      const json = await res.json();
      const dish = json && json.data && json.data.dish;
