Paste this whole script in the DevTools console while you’re on the **Menus** page and hit Enter:

```js
(async () => {
  const GRAPHQL_ENDPOINT = "/graphql";
  const csrf = document.querySelector('meta[name="csrf-token"]')?.content || null;

  // Our custom query: same as menusDish but trimmed down to what we need
  const query = `
    query menusDishModTags($menuItemId: Int!) {
      dish(menuItemId: $menuItemId) {
        id
        name
        selectedMenuItem {
          id
          name
          modifierGroups {
            id
            name
          }
        }
      }
    }
  `;

  // Find all menu-item cards in the current section
  const cards = Array.from(
    document.querySelectorAll('[data-cy="menu_item_card"]')
  );

  console.log(`[MOD-TAGS] Found ${cards.length} menu item cards`);

  for (const card of cards) {
    const idAttr = card.id || card.getAttribute("id");
    const match = idAttr && idAttr.match(/^(\d+)-menu-item$/);

    if (!match) {
      console.warn("[MOD-TAGS] Could not extract menuItemId from", idAttr);
      continue;
    }

    const menuItemId = parseInt(match[1], 10);

    // Avoid duplicating tags if you run the script multiple times
    if (card.querySelector(".mod-tag-container")) {
      console.log("[MOD-TAGS] Tags already present for", menuItemId);
      continue;
    }

    console.log("[MOD-TAGS] Processing menuItemId", menuItemId);

    // Where to inject the tags – under the name/description block
    const headerContent =
      card.querySelector(".MuiCardHeader-content-2135") || card;

    const tagContainer = document.createElement("div");
    tagContainer.className = "mod-tag-container";
    tagContainer.style.marginTop = "4px";
    tagContainer.style.display = "flex";
    tagContainer.style.flexWrap = "wrap";
    tagContainer.style.gap = "4px";
    tagContainer.style.fontSize = "11px";
    tagContainer.style.alignItems = "center";

    tagContainer.textContent = "Loading modifiers…";
    headerContent.appendChild(tagContainer);

    const body = {
      operationName: "menusDishModTags",
      query,
      variables: { menuItemId },
    };

    try {
      console.log("[MOD-TAGS] Sending fetch for menuItemId", menuItemId);

      const resp = await fetch(GRAPHQL_ENDPOINT, {
        method: "POST",
        credentials: "same-origin",
        headers: {
          "content-type": "application/json",
          ...(csrf ? { "x-csrf-token": csrf } : {}),
        },
        body: JSON.stringify(body),
      });

      console.log(
        "[MOD-TAGS] Response status for",
        menuItemId,
        ":",
        resp.status
      );

      if (!resp.ok) {
        console.error("[MOD-TAGS] HTTP error for", menuItemId, resp.status);
        tagContainer.textContent = "Tags: error";
        tagContainer.style.opacity = "0.7";
        continue;
      }

      const json = await resp.json();
      console.log("[MOD-TAGS] GraphQL JSON for", menuItemId, json);

