    query menusDishModTags($menuItemId: Int!) {
      dish(menuItemId: $menuItemId) {
        id
        name
        modifierGroups {
          id
          name
          minSelectionsCount
          maxSelectionsCount
        }
      }
    }
  `;

  const formatRangeTooltip = (group) => {
    const min = group.minSelectionsCount;
    const max = group.maxSelectionsCount;

    const parts = [];
    if (typeof min === "number") parts.push(`Min: ${min}`);
    if (typeof max === "number") parts.push(`Max: ${max}`);
    return parts.join(" • ");
  };

  async function fetchDishData(menuItemId) {
    if (dishCache.has(menuItemId)) {
      console.log("[MOD-TAGS] Using cached dish for", menuItemId);
      return dishCache.get(menuItemId);
    }

    const body = {
      operationName: "menusDishModTags",
      query,
      variables: { menuItemId },
    };

    console.log("[MOD-TAGS] Fetching dish data for", menuItemId, body);

    const resp = await fetch(GRAPHQL_ENDPOINT, {
      method: "POST",
      credentials: "same-origin",
      headers: {
        "content-type": "application/json",
        ...(csrf ? { "x-csrf-token": csrf } : {}),
      },
      body: JSON.stringify(body),
    });

    console.log("[MOD-TAGS] Response status for", menuItemId, resp.status);

    if (!resp.ok) {
      throw new Error(`HTTP ${resp.status}`);
    }

    const json = await resp.json();
    console.log("[MOD-TAGS] GraphQL JSON for", menuItemId, json);

    const dish = json?.data?.dish || null;
    dishCache.set(menuItemId, dish);
    return dish;
  }

  async function tagCard(card) {
    try {
      const idAttr = card.id || card.getAttribute("id");
      const match = idAttr && idAttr.match(/^(\d+)-menu-item$/);

      if (!match) {
        console.warn("[MOD-TAGS] Could not extract menuItemId from", idAttr);
        return;
      }

      const menuItemId = parseInt(match[1], 10);
      console.log("[MOD-TAGS] Tagging card for menuItemId", menuItemId, card);

      // Avoid duplicating the container
      let tagContainer = card.querySelector(".mod-tag-container");
      if (!tagContainer) {
        const headerContent =
          card.querySelector(".MuiCardHeader-content-2135") || card;

        tagContainer = document.createElement("div");
        tagContainer.className = "mod-tag-container";
        tagContainer.style.marginTop = "4px";
        tagContainer.style.display = "flex";
        tagContainer.style.flexWrap = "wrap";
        tagContainer.style.gap = "4px";
        tagContainer.style.fontSize = "11px";
        tagContainer.style.alignItems = "center";

        headerContent.appendChild(tagContainer);
      } else {
        // Clear if re-tagging
        tagContainer.textContent = "";
      }

      tagContainer.textContent = "Loading modifiers…";

      const dish = await fetchDishData(menuItemId);

      const modifierGroups = dish?.modifierGroups || [];
      console.log(
        "[MOD-TAGS] modifierGroups for",
        menuItemId,
        "count:",
        modifierGroups.length,
        modifierGroups
      );

      tagContainer.textContent = "";

      if (!modifierGroups.length) {
        tagContainer.textContent = "No modifiers";
        tagContainer.style.opacity = "0.6";
        return;
      }

      for (const group of modifierGroups) {
        if (!group || !group.name) continue;

        const span = document.createElement("span");
        span.textContent = group.name;
        span.style.border = "1px solid rgba(0,0,0,0.2)";
        span.style.borderRadius = "9999px";
        span.style.padding = "1px 6px";
        span.style.background = "#f5f5f5";
        span.style.whiteSpace = "nowrap";

        const tooltip = formatRangeTooltip(group);
        if (tooltip) {
          span.title = tooltip; // native tooltip on hover
        }

        tagContainer.appendChild(span);
      }
    } catch (err) {
      console.error("[MOD-TAGS] Error tagging card", card, err);
      const existing = card.querySelector(".mod-tag-container");
      if (existing) {
        existing.textContent = "Tags: error";
        existing.style.opacity = "0.7";
      }
    }
  }

  function scanAndTagAll() {
    const cards = Array.from(
      document.querySelectorAll('[data-cy="menu_item_card"]')
    );
    console.log("[MOD-TAGS] scanAndTagAll found", cards.length, "cards");
    cards.forEach((card) => tagCard(card));
  }

  // Initial run
  scanAndTagAll();

  // MutationObserver to handle lazy-loaded items
  const observer = new MutationObserver((mutations) => {
    for (const m of mutations) {
      for (const node of m.addedNodes) {
        if (!(node instanceof HTMLElement)) continue;

        // If the added node itself is a card
        if (node.matches?.('[data-cy="menu_item_card"]')) {
          console.log("[MOD-TAGS] New card node detected", node);
          tagCard(node);
        }

        // Or if it contains cards inside
        const innerCards = node.querySelectorAll?.(
          '[data-cy="menu_item_card"]'
        );
        if (innerCards && innerCards.length) {
          console.log(
            "[MOD-TAGS] New subtree with",
            innerCards.length,
            "cards detected"
          );
          innerCards.forEach((c) => tagCard(c));
        }
      }
    }
  });

  observer.observe(document.body, {
    childList: true,
    subtree: true,
  });

  // Expose observer so you can stop it if needed
  window.modTagsObserver = observer;

  console.log("[MOD-TAGS] Observer attached. Auto-tagging enabled.");
})();
```

Now as you scroll and new menu items pop in, they should get their modifier tags (with hover min/max) automatically.  

If you want to refine the tooltip text later (e.g. “Choose exactly 2” when min=max>0), we can add that logic on top of the same `minSelectionsCount` / `maxSelectionsCount` values.

